var uriManager = {};

uriManager.version = '1';

uriManager.object = function (uri) {

  if (!uriManager._check(uri)) {
    return false;
  } else {
    var _arr = uri.split('/');
    _arr.shift();
    if(_arr[_arr.length-1]===''){
      _arr.pop();
    }
    return _arr;
  }
};
/**
 *
 * @param uri string /somebody/field/prefixed
 * @private
 */
uriManager._check = function (uri) {
  return ( typeof uri == 'string' ) && ( uri[0] == '/' );
};

module.exports = uriManager;


